import React, { Component, createRef } from 'react'

export default class SearchBar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: ""
        };

        this.inputRef = createRef();
        this.onChange = this.onChange.bind(this);

    }

    onChange(e) {
        this.setState((old) => {
            let newValue = old.value+e;
            return {
                state: newValue
            }
        })
    }

  render() {
    return (
      <div className="searchbar">
          <input type="text" ref={this.inputRef} onChange={(e) => this.onChange(e)} value={this.state.value} className="searchbar__input"/>
          <a className="searchbar__btn"><img className="searchbar_arrow" src="/images/icon-arrow.svg"/></a>
      </div>
    )
  }
}
